chure.\r\n', 'Taxes', '', 'inherit', 'open', 'open', '', '50-revision-2', '', '', '2011-09-27 19:42:23', '2011-09-27 19:42:23', '', 50, 'http://www.stregispark.net/?p=53', 0, 'revision', '', 0);
